import React from 'react';
// import Heart from '../../../assets/white.png';
import Link from 'next/link';
import Image from '../../UI/Image/Image';
import Button from '../../UI/Button/Button';

const post = (props)=>(
        <div className="row">
                <h6>{props.name}</h6>
                <div className="container">
                    <Image image={props.image}/>    
                    <div className="overlay">
                        <Link href={{ pathname: 'http://localhost:8080/add', query: { name: 'Zeit' } }}>
                            <Button>fa fa-fw fa-heart</Button>
                        </Link>
                    </div>
                </div>

                <style jsx>
                {`
                    h6{
                        color: blue;
                    }
                    img {
                        max-width: 100%;
                        height: auto;
                    }
                    button{
                        position: absolute;
                        top: 90%;
                        left: 80%;
                        transform: translate(-50%, -50%);
                        -ms-transform: translate(-50%, -50%);
                        background-color: #555;
                        color: white;
                        font-size: 16px;
                        padding: 12px 24px;
                        border: none;
                        cursor: pointer;
                        border-radius: 5px;
                    }
                    .container {
                        position: relative;
                        width: 100%;
                        max-width: 400px;
                    }
                    .overlay {
                        position: absolute;
                        top: 0;
                        bottom: 0;
                        left: 0;
                        right: 0;
                        height: 100%;
                        width: 100%;
                        opacity: 0;
                        transition: .3s ease;
                        background-color: red;
                     }
                `}    
                </style>
                <hr/>
        </div>
);



export default post;
