import React from 'react';
import Post from './Post/Post';

const controls = [
    {label:'Salad', image:'https://homepages.cae.wisc.edu/~ece533/images/airplane.png'},
    {label:'Bacon', image:'https://homepages.cae.wisc.edu/~ece533/images/arctichare.png'},
    {label:'Cheese', image:'https://homepages.cae.wisc.edu/~ece533/images/boat.png'},
    {label:'Meat', image:'https://homepages.cae.wisc.edu/~ece533/images/cat.png'},
];

const posts = (props)=>(
    <div>
    <div>
        {props.postsData.map(ctrl=>(
            <Post 
                key={ctrl.username} 
                name={ctrl.username}
                image={ctrl.image}/>
        ))}
        
    </div>
    <style jsx>
    {`
        div {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin: 5px 0;
        }
        @keyframes enable {
            0% {
                transform: scale(1);
            }
            60% {
                transform: scale(1.1);
            }
            100% {
                transform: scale(1);
            }
        }
    `}    
    </style>
    </div>
);



export default posts;
