import React from 'react';

const post = (props)=>(
        <div class="row">
            <div class="col-md-12">
                <h5>{props.name}</h5>
                <img src={props.image} alt=""/>
                <button >fav</button>
                <style jsx>
                {`
                    h5{
                        color: blue;
                    }
                    img {
                        max-width: 100%;
                        height: auto;
                    }
                    button{
                        position: absolute;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%);
                        -ms-transform: translate(-50%, -50%);
                        background-color: #555;
                        color: white;
                        font-size: 16px;
                        padding: 12px 24px;
                        border: none;
                        cursor: pointer;
                        border-radius: 5px;
                     }
                `}    
                </style>
             </div>
        </div>
);



export default post;
