import React from 'react';

const post = (props)=>(
    <div class="container">
    <div class="row">
      <div class="col-sm-12">
      <h4>{props.name}</h4>
      <img src={props.image} alt=""/>
      <button >fav</button>
      <style jsx>
      {`
          h4{
              color: blue;
          }
          img {
              max-width: 100%;
              height: auto;
          }
          @media (max-width: 600px) {
              div {
                  background: white;
              }
              img{
                  max-width:auto;
              }
          }
      `}    
      </style>

      </div>
    </div>
    </div>
   
);



export default post;
