import Layout from '../components/Layout/Layout';
import Posts from '../components/Posts/Posts';
import fetch from 'isomorphic-unfetch';



const Home = (props) => {
    const data = props;
    // const data = [
    //     { "username": "tracymcgrady", "image": "https://homepages.cae.wisc.edu/~ece533/images/airplane.png" },
    //     { "username": "theodorelincoln", "image": "https://homepages.cae.wisc.edu/~ece533/images/arctichare.png" }
    // ]
    console.log('.data : ' , props.data);
    //console.warn("data", props.data)
    return <div className="container">
        <div className="row">
            <div className="col-md-12">
                <Layout>
                    <Posts postsData={data} />
                </Layout>
            </div>
        </div>
    </div>


}

Home.getInitialProps = async (res) => {
    console.log("called")
    let responseData;
    await fetch('http://localhost:8080/').then((resp) => {
        console.log("Promis return")
        let rrr = resp.json();
        rrr.then((body) =>{
            responseData = body.data;
            console.log('.data : ' , responseData);
            return {test:responseData};
        });
    }).catch((error) => {
        console.log('Erro : ', error);
    })
    console.log("after promis : " , responseData)
    return {test:responseData};
    // const data = await res.json({something:'LKasdadsad'})
    //return { stars: json.stargazers_count } 

    //return{data}
}


export default Home;
