import React ,{ Component } from 'react';
// import Heart from '../../../assets/white.png';
import Link from 'next/link';
import Image from '../../UI/Image/Image';
import Button from '../../UI/Button/Button';
import Title from '../../UI/Title/Title';
import Router from 'next/router';

class Post extends Component{
    async addFavoriteItem (id) {
        console.log(id);
        const res=  await fetch('http://localhost:8080/add?name=tharaka')
        const json = await res.json()
      }
    render(){
        return(
            <div className="row">
            <Title username={this.props.name}/>
            <Image image={this.props.image}/>     
            {/* <Link href={{ pathname: 'http://localhost:8080/add', query: { name: 'Zeit' } }}>
                <a><Button>fa fa-fw fa-heart</Button></a>
            </Link>                             */}
           
                <a onClick={this.addFavoriteItem(this.props.key)}><Button>fa fa-fw fa-heart</Button></a>
        
            <style jsx>
            {`
                img {
                    max-width: 100%;
                    height: auto;
                }
                a{
                    position: absolute;
                    
                }
            
            `}    
            </style>
            <hr/>
    </div>
    );
}}



export default Post;
