import React from 'react';
// import Heart from '../../../assets/white.png';
import Link from 'next/link';
import Image from '../../UI/Image/Image';
import Button from '../../UI/Button/Button';

const post = (props)=>(
        <div className="row">
                <h6>{props.name}</h6>
               
                    
                        
                   
                        <div className="con">
                        <Image image={props.image}/>     
                        <div className="overlay">
                        <Link href={{ pathname: 'http://localhost:8080/add', query: { name: 'Zeit' } }}>
                            <a><Button>fa fa-fw fa-heart</Button></a>
                        </Link>
                        </div>
                      </div> 

                <style jsx>
                {`
                    h6{
                        color: blue;
                    }
                    img {
                        max-width: 100%;
                        height: auto;
                    }
                    a{
                        position: absolute;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%);
                        -ms-transform: translate(-50%, -50%);
                        
                    }
                    .con{
                        position: relative;
                        width: 100%;
                        max-width: 400px;
                    }
                    .overlay {
                        position: absolute;
                        top: 0;
                        bottom: 0;
                        left: 0;
                        right: 0;
                        height: 100%;
                        width: 100%;
                        opacity: 0;
                        transition: .3s ease;
                       
                        }
                
                `}    
                </style>
                <hr/>
        </div>
);



export default post;
