import React from 'react';
import Heart from '../../../assets/white.png';

const post = (props)=>(
        <div className="row">
                <h5>{props.name}</h5>
                <img src={props.image} alt=""/>
                <button ><img src="" alt="" /></button>
                
                <style jsx>
                {`
                    h5{
                        color: blue;
                    }
                    img {
                        max-width: 100%;
                        height: auto;
                    }
                    button{
                        position: absolute;
                        top: 90%;
                        left: 80%;
                        transform: translate(-50%, -50%);
                        -ms-transform: translate(-50%, -50%);
                        background-color: #555;
                        color: white;
                        font-size: 16px;
                        padding: 12px 24px;
                        border: none;
                        cursor: pointer;
                        border-radius: 5px;
                    }
                `}    
                </style>
                <hr/>
        </div>
);



export default post;
